import React, { useState, useEffect, useRef } from 'react';
import mobileSvg from './image.png'
import mobileSvgImage from './imageForMobileSvg.jpg' 
import desktopImage from './desktop2.png'

// import './Subhero.css'


let animationFrameId;


const Subhero = () => {




    useEffect(() => {
        const subhero = document.querySelector('#subhero');
        const subheroMainSvg = document.querySelector('#subhero-main')
       

        const animateMainSvg = (bounds, scrollTopNormalized, scrollTopEnd) => {
            let rotateX = ((1 - (scrollTopNormalized / scrollTopEnd)) * 45).toFixed(1)
            let translateZ = ((1 - (scrollTopNormalized / scrollTopEnd)) * 300).toFixed(1) // 500 is the total range (300 - -200)
            let blurValue = ((1 - (scrollTopNormalized / scrollTopEnd)) * 30).toFixed(1)
            let brightness = (((scrollTopNormalized / scrollTopEnd)) * 1).toFixed(1)
            
            rotateX = Math.max(0, Math.min(rotateX, 45));
            translateZ = Math.max(0, Math.min(translateZ, 300));
            // translateZ = Math.max(0, Math.min(translateZ, 100));
            blurValue = Math.max(0, Math.min(blurValue, 30));
            brightness = Math.max(0, Math.min(brightness, 1));

            // console.log('animating', scrollTopNormalized)
            if(bounds.top < 0) {
                // console.log('animate the other way')

                // rotateX = rotateX * -1
                // subheroMainSvg.style.transformOrigin = 'center 70%';
                
            } else if(bounds.top > 0) {
                // console.log('animate one way')
                
                subheroMainSvg.style.transformOrigin = 'center 30%';
                subheroMainSvg.style.transform = `translateZ(${translateZ}px) rotateX(${rotateX}deg)`;
                subheroMainSvg.style.filter = `brightness(${brightness}) blur(${blurValue}px)`;
                // subheroMainSvg.style.transform = `translateZ(${translateZ}px)`;
                // subheroMainSvg.style.filter = `brightness(${brightness})`;
            }

        }
       

        // Store the previous intersection ratio
        let previousScollY = 0;
        
        const handleScroll = () => {


            
            // Calculate the rotateX value based on the intersection ratio
            // 0 intersection -> 45deg, 0.8 intersection -> 0deg

            // console.log('rootbounds height: ', entry.rootBounds.height, 'rootBounds width: ', entry.rootBounds.width)
            // console.log('window height: ', window.innerHeight, 'window width: ', window.innerWidth)
            const bounds = subhero.getBoundingClientRect()
            // console.log('entry bounding client y: ', entry.boundingClientRect.y, ' bounding client y: ', subhero.getBoundingClientRect().y)

            let scrollTopNormalized = bounds.y /  window.innerHeight 
            scrollTopNormalized = Math.max(0, Math.min(scrollTopNormalized, 1))
            scrollTopNormalized = 1 - scrollTopNormalized

            let scrollTopEnd = 0.6
           


            animateMainSvg(bounds, scrollTopNormalized, scrollTopEnd)


            // console.log('animating', scrollTopNormalized)
            if(bounds.top < 0) {
                // console.log('animate the other way')

                // rotateX = rotateX * -1
                
            } else if(bounds.top > 0) {
                // console.log('animate one way')




                let endValue = 0.6
                let blur = ((1 - (scrollTopNormalized / endValue)) * 10).toFixed(1)
                blur = Math.max(0, Math.min(blur, 10));

                document.querySelector('#subhero-header').style.filter = `blur(${blur}px`
                document.querySelector('#subhero-badge').style.filter = `blur(${blur}px`
            }



            


            
            // if(animationFrameId) return
            // animationFrameId = requestAnimationFrame(() => {
                // subheroMainSvg.style.transform = `translateZ(${translateZ}px) rotateX(${rotateX}deg)`;
                // subheroMainSvg.style.filter = `brightness(${brightness}) blur(${blurValue}px)`;
            // })
            previousScollY = window.scrollY
        }





        
        window.addEventListener('scroll', handleScroll)

        return () => {
            window.removeEventListener('scroll', handleScroll)
        };

    }, [])

    return (
        <div id='subhero'>

            <div id='subhero-badge' className='badge'>
                <div>
                    <LayoutIcon />Layout
                </div>
            </div>
            <div id="subhero-header" className='h2'>
                <span>Design.&nbsp;</span> 
            
                <div>Tailored&nbsp;</div>
                <div>for&nbsp;</div>
                <div>MacOS</div>
                <div>.</div>
            </div>
            
                
            <div id="subhero-main">
                {/* <MainSvg /> */}
                <div id="subhero-main-svg-desktop">
                  <img src={desktopImage} />
                </div>
                <div id="subhero-main-svg-mobile">
                     <img src={mobileSvgImage} />
                     <MobileSvg />
                </div>
            </div>

            {/* <div id='subhero-scenes'>
                <ScenesSvg />
            </div> */}
                
           
            {/* <div id='main'>
                <div className='badge'>
                    <div>
                        <LayoutIcon />Layout
                    </div>
                </div>
                <div className='h2'><span>Design.</span> Tailored for MacOS.</div>
            </div> */}
        </div>
    );
};
export { Subhero }




/**
 * Maps an input value from one range to another.
 * 
 * @param {number} inputValue - The value to be mapped from the input range.
 * @param {number} in_min - The lower bound of the input range.
 * @param {number} in_max - The upper bound of the input range.
 * @param {number} out_min - The lower bound of the output range.
 * @param {number} out_max - The upper bound of the output range.
 * @returns {number} - The value mapped to the output range.
 */
function mapRange(inputValue, in_min, in_max, out_min, out_max) {
    return (inputValue - in_min) * (out_max - out_min) / (in_max - in_min) + out_min;
}
  




